CREATE TABLE `AUTH` (  `AUTH_ID` varchar(50) NOT NULL COMMENT '권한코드',  `AUTH_NM` varchar(200) DEFAULT NULL COMMENT '권한명',  PRIMARY KEY (`AUTH_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='권한정보';CREATE TABLE `AUTH_LVL` (  `PARENT_AUTH_ID` varchar(50) NOT NULL COMMENT '상위권한코드',  `CHILD_AUTH_ID` varchar(50) NOT NULL COMMENT '하위권한코드',  PRIMARY KEY (`PARENT_AUTH_ID`,`CHILD_AUTH_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='권한계층정보';CREATE TABLE `CORP_AUTH` (  `CORP_ID` varchar(20) NOT NULL COMMENT '회사아이디',  `AUTH_ID` varchar(50) NOT NULL COMMENT '권한코드',  PRIMARY KEY (`CORP_ID`,`AUTH_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='회사-권한';CREATE TABLE `CORP_INFO` (  `CORP_ID` varchar(20) NOT NULL COMMENT '회사아이디',  `CORP_NM` varchar(50) NOT NULL COMMENT '회사명',  `CORP_LOGO_PATH` varchar(30) DEFAULT NULL COMMENT '로고파일위치',  `CORP_LOGO_NM` varchar(50) DEFAULT NULL COMMENT '로고파일',  `CORP_LOGO_EXT` varchar(5) DEFAULT NULL COMMENT '로고파일확장자',  `CORP_LOGO_SIZE` bigint(20) DEFAULT NULL COMMENT '로고파일사이즈',  `CORP_LOGO_CONT_TYPE` varchar(30) DEFAULT NULL COMMENT '로고파일컨텐츠타입',  `CORP_LOGO_ORG_FILE_NM` varchar(50) DEFAULT NULL COMMENT '로고고유파일명',  `CORP_URL` varchar(50) DEFAULT NULL COMMENT '홈페이지주소',  `CORP_TEL` varchar(15) DEFAULT NULL COMMENT '전화번호',  `CORP_POST` varchar(7) DEFAULT NULL COMMENT '우편번호',  `CORP_ADR` varchar(150) DEFAULT NULL COMMENT '주소',  `CORP_ADR_DSC` varchar(100) DEFAULT NULL COMMENT '상세주소',  `UP_CORP_ID` varchar(20) DEFAULT NULL COMMENT '상위회사아이디',  `MOD_DT` date NOT NULL COMMENT '수정일',  `MOD_ID` varchar(20) NOT NULL COMMENT '수정자아이디',  `MOD_IP` varchar(80) NOT NULL COMMENT '수정자아이피',  `REG_DT` date NOT NULL COMMENT '등록일',  `REG_ID` varchar(20) NOT NULL COMMENT '등록자아이디',  `REG_IP` varchar(80) NOT NULL COMMENT '등록자아이피',  PRIMARY KEY (`CORP_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='회사정보';CREATE TABLE `RESO_AUTH` (  `RESO_ID` varchar(50) NOT NULL COMMENT '리소스아이디',  `AUTH_ID` varchar(50) NOT NULL COMMENT '권한코드',  `AUTH_NM` varchar(200) DEFAULT NULL COMMENT '권한명',  PRIMARY KEY (`RESO_ID`,`AUTH_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='리소스-권한';CREATE TABLE `RESO_INFO` (  `RESO_ID` varchar(50) NOT NULL COMMENT '리소스아이디',  `RESO_NM` varchar(200) DEFAULT NULL COMMENT '리소스명',  `RESO_PTN` varchar(200) DEFAULT NULL COMMENT '리소스패턴',  `RESO_TP` varchar(10) DEFAULT NULL COMMENT '리소스타입',  `RESO_ORD` tinyint(4) DEFAULT NULL COMMENT '순서',  PRIMARY KEY (`RESO_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='리소스 정보';CREATE TABLE `USER_AUTH` (  `USER_ID` varchar(20) NOT NULL COMMENT '사용자아이디',  `AUTH_ID` varchar(50) NOT NULL COMMENT '권한코드',  PRIMARY KEY (`USER_ID`,`AUTH_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='사용자-권한';CREATE TABLE `USER_INFO` (  `USER_ID` varchar(20) NOT NULL COMMENT '사용자아이디',  `CORP_ID` varchar(20) DEFAULT NULL COMMENT '회사아이디',  `USER_NM` varchar(50) NOT NULL COMMENT '이름',  `USER_PW` varchar(50) NOT NULL COMMENT '패스워드',  `USER_EMAIL` varchar(50) DEFAULT NULL COMMENT '이메일',  `USER_HP` varchar(15) DEFAULT NULL COMMENT '휴대전화번호',  `USER_TEL` varchar(15) DEFAULT NULL COMMENT '일반전화번호',  `USER_AUTH` char(1) NOT NULL COMMENT '회원등급(0:슈퍼관리자,1:회사정보관리자,2:셋톱관리자)',  `USE_YN` char(1) NOT NULL COMMENT '사용여부',  `ACCESS_FAIL_CNT` tinyint(4) DEFAULT NULL COMMENT '로그인실패횟수',  `MOD_DT` date NOT NULL COMMENT '수정일',  `MOD_ID` varchar(20) NOT NULL COMMENT '수정자아이디',  `MOD_IP` varchar(80) NOT NULL COMMENT '수정자아이피',  `REG_DT` date NOT NULL COMMENT '등록일',  `REG_ID` varchar(20) NOT NULL COMMENT '등록자아이디',  `REG_IP` varchar(80) NOT NULL COMMENT '등록자아이피',  PRIMARY KEY (`USER_ID`),  KEY `FK_CORP_INFO_TO_USER_INFO` (`CORP_ID`),  CONSTRAINT `FK_CORP_INFO_TO_USER_INFO` FOREIGN KEY (`CORP_ID`) REFERENCES `CORP_INFO` (`CORP_ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='사용자';